Interface.Script:
//OnInit.Register(InitFunction);
//OnLoad.Register(LoadFunction);
//OnSave.Register(SaveFunction);
OnSave.Register(GetRule)
OnLoad.Register(LoadData)
OnInit.Register(InitData)
function GetRule() {
var RecRule = "";
RecRule += "DTSTART:@STARTDATE\r\n";
RecRule += "DTEND:@ENDDATE\r\n";
RecRule += GetRuleType();
$("#hdnRecur").val(RecRule);

}
function GetRuleType() {
var RRule = "";
if ($("#rbtnHourly").checked()) {
RRule = "RRULE:FREQ=HOURLY";
RRule = getEndby(RRule);
RRule += ";INTERVAL=" + $("#txtEveryHour").val() + "\r\n";
}
else if ($("#rbtnDaily").checked()) {
RRule = "RRULE:FREQ=DAILY";
RRule = getEndby(RRule);
if ($("#rbtnday").checked()) {
RRule += ";INTERVAL=" + $("#txtEveryday").val() + ";BYDAY=SU,MO,TU,WE,TH,FR,SA" + "\r\n";
}
else {
RRule += ";BYDAY=MO,TU,WE,TH,FR" + "\r\n";
}
}
else if ($("#rbtnWeekly").checked()) {
RRule = "RRULE:FREQ=WEEKLY";
RRule = getEndby(RRule);
RRule += ";INTERVAL=" + $("#txtEveryWeek").val();
RRule += ";BYDAY=";
var Checkday = "";
if ($("#chkSun").checked())
Checkday += "SU,";
if ($("#chkMon").checked())
Checkday += "MO,";
if ($("#chkTue").checked())
Checkday += "TU,";
if ($("#chkWed").checked())
Checkday += "WE,";
if ($("#chkThur").checked())
Checkday += "TH,";
if ($("#chkFri").checked())
Checkday += "FR,";
if ($("#chkSat").checked())
Checkday += "SA,";
Checkday = Checkday.substring(0, Checkday.length - 1);
RRule += Checkday;
RRule += ";WKST=" + "\r\n";
}
else if ($("#rbtnMonthly").checked()) {
RRule = "RRULE:FREQ=MONTHLY";
RRule = getEndby(RRule);
if ($("#radMonthtype1").checked()) {
RRule += ";INTERVAL=" + $("#txtmonthcount").val() + ";BYMONTHDAY=" + $("#txtdaycount").val();
}
else {
RRule += ";INTERVAL=" + $("#txtmonthcount2").val() + ";BYSETPOS=" + $("#radDayCount>option:selected").val() + ";BYDAY=" + $("#radDayType>option:selected").val();
}
}
else if ($("#rbtnYearly").checked()) {
RRule = "RRULE:FREQ=YEARLY";
RRule = getEndby(RRule);
if ($("#radYearType1").checked()) {
RRule += ";BYMONTHDAY=" + $("#txtYearDayCount").val() + ";BYMONTH=" + $("#radmonth>option:selected").val();
}
else {
RRule += ";BYSETPOS=" + $("#radYearDayCount>option:selected").val() + ";BYDAY=" + $("#radYearDayType>option:selected").val() + ";BYMONTH=" + $("#radYearmonth>option:selected").val();
}
}
return RRule;
}
function getEndby(RRule) {
if ($("#radEndAfter").checked()) {
RRule += ";COUNT=" + $("#txtOccurrence").val();
}
else if ($("#radEndBy").checked()) {
//var date = $("#datepickerEndBy").val().replace(/\-/g, "");
var date = $("#datepickerEndBy").val().split('/');
RRule += ";UNTIL=" + date[2] + date[1] + date[0] + "T000000Z";
}
return RRule;
}
function ShowPanel(type) {
$("#Hourly").css("display", "none");
$("#Daily").css("display", "none");
$("#Weekly").css("display", "none");
$("#Monthly").css("display", "none");
$("#Yearly").css("display", "none");
$("#" + type).css("display", "block");
}
function LoadData() {
$("#Hourly").css("display", "none");
$("#Daily").css("display", "none");
$("#Weekly").css("display", "none");
$("#Monthly").css("display", "none");
$("#Yearly").css("display", "none");
var a = $("#hdnRecur").val();
var rule = a.split("\n")[2];
//var rule = "RRULE:FREQ=YEARLY;UNTIL=20140410T000000Z;BYSETPOS=4;BYDAY=MO,TU,WE,TH,FR;BYMONTH=5";
if (rule.indexOf("HOURLY") > -1) {
$("#rbtnHourly").attr("checked", "checked");
$("#Hourly").css("display", "block");
SetHourlyData(rule);
SetEndBy(rule);
}
else if(rule.indexOf("DAILY") > -1)
{
$("#rbtnDaily").attr("checked", "checked");
$("#Daily").css("display", "block");
SetDailyyData(rule);
SetEndBy(rule);
}
else if(rule.indexOf("WEEKLY") > -1)
{
$("#rbtnWeekly").attr("checked", "checked");
$("#Weekly").css("display", "block");
SetWeeklyData(rule);
SetEndBy(rule);
}
else if(rule.indexOf("MONTHLY") > -1)
{
$("#rbtnMonthly").attr("checked", "checked");
$("#Monthly").css("display", "block");
SetMonthlyData(rule);
SetEndBy(rule);
}
else if (rule.indexOf("YEARLY") > -1)
{
$("#rbtnYearly").attr("checked", "checked");
$("#Yearly").css("display", "block");
SetYearlyData(rule);
SetEndBy(rule);
}
}
function SetEndBy(rule) {
var Hash = GetHashRuleValue(rule);
if (rule.indexOf("COUNT") > -1) {
$("#radEndAfter").attr("checked", "checked");
$("#txtOccurrence").val(Hash.COUNT)
}
else if (rule.indexOf("UNTIL") > -1) {
$("#radEndBy").attr("checked", "checked");
var dtEnddate = Hash.UNTIL.substring(0, 8);
var newdate = dtEnddate.substring(6) + "/" + dtEnddate.substring(4, 6) + "/" + dtEnddate.substring(0, 4)
$("#datepickerEndBy").val(newdate);
}
else {
$("#radNoEndDate").attr("checked", "checked");
}
}
function SetHourlyData(rule) {
var Hash = GetHashRuleValue(rule);
$("#txtEveryHour").val(Hash.INTERVAL);
}
function SetDailyyData(rule) {
var Hash = GetHashRuleValue(rule);
if (rule.indexOf("INTERVAL") > -1) {
$("#rbtnday").attr("checked", "checked");
$("#txtEveryday").val(Hash.INTERVAL)
}
else {
$("#rbtnWeekday").attr("checked", "checked");
}
}
function SetWeeklyData(rule) {
var Hash = GetHashRuleValue(rule);
$("#txtEveryWeek").val(Hash.INTERVAL)
var days = Hash.BYDAY;
if (days.indexOf("SU") > -1)
$("#chkSun").attr("checked", "checked");
if (days.indexOf("MO") > -1)
$("#chkMon").attr("checked", "checked");
if (days.indexOf("TU") > -1)
$("#chkTue").attr("checked", "checked");
if (days.indexOf("WE") > -1)
$("#chkWed").attr("checked", "checked");
if (days.indexOf("TH") > -1)
$("#cchkThur").attr("checked", "checked");
if (days.indexOf("FR") > -1)
$("#chkFri").attr("checked", "checked");
if (days.indexOf("SA") > -1)
$("#chkSat").attr("checked", "checked");
}
function SetMonthlyData(rule) {
var Hash = GetHashRuleValue(rule);
if (rule.indexOf("BYMONTHDAY") > -1) {
$("#radMonthtype1").attr("checked", "checked");
$("#txtdaycount").val(Hash.BYMONTHDAY)
$("#txtmonthcount").val(Hash.INTERVAL)
}
else {
$("#radMonthType2").attr("checked", "checked");
$("select#radDayCount").val(Hash.BYSETPOS);
$("select#radDayType").val(Hash.BYDAY);
//$("select#radDayCount").find("option#" + Hash.BYSETPOS).attr("selected", "selected");
//$("select#radDayType").find("option#" + Hash.BYDAY).attr("selected", "selected");
$("#txtmonthcount2").val(Hash.INTERVAL);
}
}
function SetYearlyData(rule) {
var Hash = GetHashRuleValue(rule);
if (rule.indexOf("BYMONTHDAY") > -1) {
$("#radYearType1").attr("checked", "checked");
//$("select#radmonth").find("option#" + Hash.BYMONTH).attr("selected", "selected");
$("select#radmonth").val(Hash.BYMONTH);
$("#txtYearDayCount").val(Hash.BYMONTHDAY)
}
else {
$("#radYearType2").attr("checked", "checked");
//$("select#radYearDayCount").find("option#" + Hash.BYSETPOS).attr("selected", "selected");
//$("select#radYearDayType").find("option#" + Hash.BYDAY).attr("selected", "selected");
//$("select#radYearmonth").find("option#" + Hash.BYMONTH).attr("selected", "selected");
$("select#radYearDayCount").val(Hash.BYSETPOS);
$("select#radYearDayType").val(Hash.BYDAY);
$("select#radYearmonth").val(Hash.BYMONTH);
}
}
function GetHashRuleValue(rule) {
var type = rule.replace("RRULE:", "").split(';');
var Hash = {
};
for (i = 0; i < type.length; i++) {
var aa = type[i].split('=');
Hash[aa[0]] = aa[1];
}
return Hash;
}
function InitData()
{
$("#datepickerEndBy").datepicker({
showOn: "button", buttonImage: "../images/date.png", buttonImageOnly: true, dateFormat: 'dd/mm/yy', scrollbar: true, dropdown: false }
);
$("#txtEveryHour").autoNumeric();
$("#txtEveryday").autoNumeric();
$("#txtEveryWeek").autoNumeric();
$("#txtmonthcount").autoNumeric();
$("#txtmonthcount2").autoNumeric();
$("#txtYearDayCount").autoNumeric();
}
